mapper.update:
update b_table
set b_title=#{b_title}, b_con=#{b_con},
b_udate=sysdate, b_eno=#{b_eno}, b_open=#{b_open}
where b_no=#{b_no}

mapper.select:
select * from ( select /*+INDEX_DESC(b_table pk_board) */ rownum rn, b_no, b_title, b_writer, b_wdate, b_udate from b_table where
b_title like '%'||#{keyword}||'%' b_con like '%'||#{keyword}||'%' b_writer like '%'||#{keyword}||'%'
rownum<=(#{pageNum} * #{amount}) ) where rn > (#{pageNum} -1)* #{amount}
